let express,mongoose,fs,cors;_15c‍.w("express",[["default",["express"],function(v){express=v}]]);_15c‍.w("mongoose",[["default",["mongoose"],function(v){mongoose=v}]]);_15c‍.w("fs",[["default",["fs"],function(v){fs=v}]]);_15c‍.w("cors",[["default",["cors"],function(v){cors=v}]]);
const morgan = require("morgan");



require("dotenv").config();

const app = express();
const port=process.env.PORT||8000;
app.use(cors());
app.use(morgan("dev"));
app.use(express().json);

// db connection 

mongoose.connect(process.env.URI,{useNewUrlParser:true,useFindAndModify:false,useUnifiedTopology:true,useCreateIndex:true,})
.then(()=>console.log("db connected"))
.catch((err)=>console.log("DB connection error"));

fs.readdirSync("./routes").map((r)=>app.use("/api",require(`./routes/${r}`)));

app.listen(port,()=>console.log(`Server is running on Port ${port}`) );
